<?php

namespace App\Admin\Actions\Change;

use Illuminate\Http\Request;
use Encore\Admin\Facades\Admin;
use Encore\Admin\Actions\BatchAction;
use Illuminate\Database\Eloquent\Collection;

class ChangeSub extends BatchAction
{
    protected $selector = '.change-sub';

    public function handle(Collection $collection, Request $request)
    {
    	if(!$request->sub) return $this->response()->error('请选择同步科目')->refresh();

        foreach ($collection as $model) {
           
        	$model->subject = $request->sub;

            $model->save();

        }

        return $this->response()->success('科目同步完成!')->refresh();
    }


    /**
     * @Author    Pudding
     * @DateTime  2020-08-25
     * @copyright [copyright]
     * @license   [license]
     * @version   [ 选择归属车型 ]
     * @return    [type]      [description]
     */
    public function form()
	{
	    $this->select('sub', '请选择归属科目')->options([1=> '科目一', 4=>'科目四']);
	}

	/**
	 * @Author    Pudding
	 * @DateTime  2020-08-25
	 * @copyright [copyright]
	 * @license   [ 页面html ]
	 * @version   [version]
	 * @return    [type]      [description]
	 */
    public function html()
    {
        return "<a class='change-sub btn btn-sm btn-danger'><i class='fa fa-info-circle'></i>更新科目</a>";
    }


    /**
     * @return string
     * 上传效果
     */
    public function handleActionPromise()
    {
        
        $resolve = <<<'SCRIPT'

        var actionResolverss = function (data) {
            $('.modal-footer').show()
            $('.tips').remove()
            var response = data[0];
            var target   = data[1];

            if (typeof response !== 'object') {
                return $.admin.swal({type: 'error', title: 'Oops!'});
            }

            var then = function (then) {
                if (then.action == 'refresh') {
                    $.admin.reload();
                }

                if (then.action == 'download') {
                    window.open(then.value, '_blank');
                }

                if (then.action == 'redirect') {
                    $.admin.redirect(then.value);
                }
            };

            if (typeof response.html === 'string') {
                target.html(response.html);
            }

            if (typeof response.swal === 'object') {
                $.admin.swal(response.swal);
            }

            if (typeof response.toastr === 'object') {
                $.admin.toastr[response.toastr.type](response.toastr.content, '', response.toastr.options);
            }

            if (response.then) {
              then(response.then);
            }
        };

        var actionCatcherss = function (request) {
            $('.modal-footer').show()
            $('.tips').remove()

            if (request && typeof request.responseJSON === 'object') {
                $.admin.toastr.error(request.responseJSON.message, '', {positionClass:"toast-bottom-center", timeOut: 10000}).css("width","500px")
            }
        };
        

SCRIPT;
    Admin::script($resolve);

    return <<<'SCRIPT'
         $('.modal-footer').hide()
         let html = `<div class='tips' style='color: red;font-size: 18px;'>更新时间取决于数据量，请耐心等待结果不要关闭窗口！<img src="data:image/gif;base64,"><\/div>`
         $('.modal-header').append(html)
process.then(actionResolverss).catch(actionCatcherss);
SCRIPT;
    
    }
}